import {
	Box,
	Breadcrumb,
	BreadcrumbItem,
	BreadcrumbLink,
	Button,
	Center,
	Container,
	Flex,
	Grid,
	Heading,
	HStack,
	Image,
	Input,
	Radio,
	RadioGroup,
	Stack,
	Text,
	useColorModeValue,
	useNumberInput,
	VStack,
} from "@chakra-ui/react";
import React from "react";
import { HelperContext } from "../context/HelperProvider";
import { TriangleDownIcon } from "@chakra-ui/icons";
import { useNavigate } from "react-router-dom";
import { ImLocation } from "react-icons/im";
import ButtonBox from "../utils/ButtonBox";
import ProceedButton from "../utils/ProceedButton";
import { CartItem } from "../utils/CartItem";

import "../App.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Mousewheel } from "swiper";
import { Scrollbar } from "swiper";
const Cart = () => {
	const { dispatch, state } = React.useContext(HelperContext);
	const [show, setShow] = React.useState(false);
	const [show2, setShow2] = React.useState(false);
	const navigate = useNavigate();
	const coupon = React.useRef<HTMLInputElement>(null);
	const subTotal = state.cartItems.reduce((acc: number, item: any) => {
		const price = item.price.slice(3).replace(/,/g, "") - 0;
		return acc + price;
	}, 0);
	const [total, setTotal] = React.useState(subTotal);

	const handleCoupon = () => {
		if (coupon?.current?.value === "deco30") {
			setShow2(true);
			setTotal(Math.floor(subTotal - subTotal * 0.3));
		}
	};
	return (
		<Container maxW={{ base: "100%", md: "90%", lg: "72%" }}>
			<Box>
				<Box
					pb='3'
					display={{ base: "flex", md: "flex", lg: "flex" }}
					justifyContent='space-between'
					mt='2.5rem'
					borderBottom='1px solid #e2e8f0'
					w='100%'
					alignItems='flex-end'
					//  flexDirection={{ base: "column", md: "row", lg: "row" }}
				>
					<Heading
						color='root.green'
						fontSize={{ base: "xl", md: "2xl", lg: "2xl" }}>
						Shopping Cart
					</Heading>
					<Breadcrumb
						justifySelf={{ base: "center", md: "flex-end" }}
						color='green.600'
						spacing='1'
						separator={
							<TriangleDownIcon
								fontSize='0.9em'
								transform='rotate(90deg)'
								color='green.600'
							/>
						}
						fontWeight='semibold'
						fontSize={{ base: "sm", md: "md", lg: "md" }}>
						<BreadcrumbItem>
							<BreadcrumbLink></BreadcrumbLink>
						</BreadcrumbItem>
						<BreadcrumbItem isCurrentPage>
							<BreadcrumbLink
								onClick={() => navigate("/inventory")}>
								Continue shopping
							</BreadcrumbLink>
						</BreadcrumbItem>
					</Breadcrumb>
				</Box>
				<Stack
					mt='5'
					justifyContent={"space-between"}
					direction={{ base: "column", md: "row", lg: "row" }}
					w='100%'
					spacing={{ base: "0", md: "5", lg: "5" }}>
					<VStack
						bg='#edf1e7'
						p='3'
						align='flex-start'
						color='black'
						width={{
							base: "100%",
							md: "100%",
							lg: "100%",
							"2xl": "20%",
						}}>
						<Flex
							alignItems={{
								base: "center",
								md: "center",
								lg: "center",
							}}>
							<ImLocation fontSize='18px' />
							<Text
								as='span'
								fontWeight={"semibold"}
								color='root.green'>
								FREE{" "}
							</Text>{" "}
							&nbsp;
							<Text> In-Store Pickup at:</Text>
						</Flex>
						<Text fontSize={"xs"}>
							DollarTree <br />
							1234 Main St. <br />
							Chesapeake, NY 10001 <br />
							(123) 456-7890{}
						</Text>
					</VStack>
					<Flex
						w='70%'
						direction={{ base: "column", md: "row", lg: "row" }}
						justifyContent='space-between'
						alignItems='center'
						display={{
							base: "none",
							md: "none",
							lg: "none",
							"2xl": "flex",
						}}>
						<Center>
							<ButtonBox
								src='data:image/svg+xml;base64,'
								src2=''
								text='Checkout'
								subtext='The safer, easier way to pay'
							/>
						</Center>
						<Center>
							<ButtonBox
								src='data:image/svg+xml;base64,'
								src2='data:image/svg+xml;base64,'
								subtext='Buy Now. pay Over Time.'
							/>
						</Center>
						<ProceedButton
							text='PROCEED TO CHECKOUT'
							subtText='First time tax-exempt order?'
							subSubText='Click PROCEED TO CHECKOUT to create an account'
						/>
					</Flex>
				</Stack>
				<Box mt={"5"}>
					<Flex
						justifyContent={"space-between"}
						alignItems={"center"}
						bg={useColorModeValue("#f2f2f2", "gray.900")}
						p={3}
						borderRadius={"sm"}
						pr='4'
						display={{ base: "none", md: "flex" }}>
						<Text flex={0.5}>ITEM</Text>
						<Text>PICK UP OR DILIVERY</Text>
						<Text>QUANTITY</Text>
						<Text>PRICE</Text>
					</Flex>
					{total ? (
						<Swiper
							direction={"vertical"}
							slidesPerView={3}
							spaceBetween={5}
							mousewheel={true}
							modules={[Mousewheel, Scrollbar]}
							className='mySwiper'>
							{state.cartItems.map((item: any, index: number) => (
								<SwiperSlide>
									<CartItem item={item} index={index} />
								</SwiperSlide>
							))}
						</Swiper>
					) : (
						<Center>
							<Image
								w='75%'
								src='https://cdni.iconscout.com/illustration/premium/thumb/empty-cart-2130356-1800917.png'
							/>
						</Center>
					)}
				</Box>
			</Box>
			<Box mt='6'>
				<Flex
					justify={"space-between"}
					direction={{ base: "column", md: "row" }}>
					<VStack w={{ base: "full", md: "395px" }} spacing={8}>
						<Text fontSize={"sm"}>
							In-Store Estimated Pick-Up dates are valid for
							orders placed before 9:30 p.m. ET.
						</Text>
						<Text fontSize={"sm"}>
							If your order is placed after 9:30 p.m. ET, the
							Estimated Pick-Up date noted on your{" "}
							<b>Order Confirmation email </b> is not guaranteed
							and could vary. Once your order has been shipped,
							your <b>Shipment Confirmation email </b> will
							include the confirmed Estimated Pick-Up date.
						</Text>
					</VStack>
					<VStack
						align={"flex-start"}
						w={{ base: "full", md: "350px" }}
						spacing={8}
						p='4'
						mt={{ base: "4", md: "0" }}>
						<Box>
							<Text
								display={"flex"}
								alignItems={"center"}
								justifyContent={"space-between"}
								w={{ base: "310px", md: "320px" }}
								fontSize={"lg"}
								bg='#edf1e7'
								p='2'
								color='root.green'
								fontWeight='semibold'>
								{" "}
								Add Promo Code
								<Button
									mr='2'
									onClick={() => setShow(!show)}
									colorScheme={"blue"}
									rounded={"full"}
									size='xs'
									p='0'
									fontSize={"md"}>
									+
								</Button>
							</Text>
							<Flex
								h='40px'
								transition={"all 0.3s ease"}
								display={show ? "flex" : "none"}>
								<Input
									type='text'
									placeholder='deco30'
									h='full'
									rounded={"none"}
									ref={coupon}
								/>
								<Button
									disabled={show2}
									rounded={"none"}
									size='sm'
									colorScheme='blue'
									h='full'
									onClick={handleCoupon}>
									Apply
								</Button>
							</Flex>
						</Box>
						<Text fontSize={"xl"} borderBottom='1px solid #edf1e7'>
							Order Summary
						</Text>
						<Flex
							justifyContent={"space-between"}
							w='full'
							borderBottom='1px solid #edf1e7'>
							<Text>Items In Your Cart:</Text>
							<Text>{state.cartItems.length}</Text>
						</Flex>
						<Flex
							justifyContent={"space-between"}
							w='full'
							borderBottom='1px solid #edf1e7'>
							<Text>Shipping:</Text>
							<Text>--</Text>
						</Flex>
						<Flex
							justifyContent={"space-between"}
							w='full'
							borderBottom='1px solid #edf1e7'>
							<Text>Estimated Tax:</Text>
							<Text>Rs 5.00</Text>
						</Flex>
						<Flex
							justifyContent={"space-between"}
							w='full'
							borderBottom='1px solid #edf1e7'>
							<Text>ESTIMATED TOTAL:</Text>
							<Text>
								Rs.{total} + 5 = Rs.{total + 5}
							</Text>
						</Flex>
						<Button
							w='full'
							bg='root.green'
							color='white'
							_hover={{ bg: "green.500" }}>
							PROCEED TO CHECKOUT
						</Button>
					</VStack>
				</Flex>
			</Box>
		</Container>
	);
};

export default Cart;
